import React from "react";
import './Todo'
const Todo = ({todo, handleRemove, handleEdit, index})=>{

    return(
        <div className="Todo">

        {todo}

        <button onClick={() => handleRemove(todo)}>X</button>
        <button onClick={() => handleEdit(index, todo)}>Edit</button>
    </div>


    )
}

export default Todo;
