import React, { useState } from "react";
import './TodoList.css';
import NewTodoForm from "./NewTodoForm";

const TodoList = () => {
    const INITIAL_STATE = [
        { todo: "Study" },
        { todo: "Workout" }
    ];
    const [todos, setTodos] = useState(INITIAL_STATE);
    const [editIndex, setEditIndex] = useState(-1);

    const handleRemove = (value) => {
        setTodos(todos.filter((todo) => todo.todo !== value));
    }

    const handleEdit = (index) => {
        setEditIndex(index);
    }

    const handleUpdate = (index, updatedTodo) => {
        const updatedTodos = [...todos];
        updatedTodos[index].todo = updatedTodo;
        setTodos(updatedTodos);
        setEditIndex(-1);
    }

    const addTask = (newTask) => {
        setTodos(items => [...items, { ...newTask }]);
    }

    return (
        <div className="TodoList">
            <h3>TO DO LIST:</h3>
            <NewTodoForm addTask={addTask} />
            <ul>
                {todos.map(({ todo }, index) => (
                    <div key={index}>
                        {editIndex === index ? (
                            <li>
                                <input
                                    type="text"
                                    value={todo}
                                    onChange={(e) => handleUpdate(index, e.target.value)}
                                />
                                <button onClick={() => handleUpdate(index, todo)}>Save</button>
                            </li>
                        ) : (
                            <li>
                                {todo}
                                <button onClick={() => handleEdit(index)}>Edit</button>
                                <button onClick={() => handleRemove(todo)}>X</button>
                            </li>
                        )}
                    </div>
                ))}
            </ul>
        </div>
    );
}

export default TodoList;
