import React, {useState} from "react";


const TodoList =()=>{
    const INITIAL_STATE=['study', 'workout']
    const [todos, setTodos]= useState(INITIAL_STATE);
    const handleRemove= (e)=>{
        const target = `${e.target.parentNode.innerText.split(" ")[0]}`;
        const index = todos.indexOf(target);
        todos.splice(index,1);
        // const index  = todos.indexOf(e.target);
        // todos.splice(index,1)


    }

    return (
        <div>
            <h3>TO DO LIST:</h3>
        <ul>
            <div>
                {todos.map(todo=> <li> {todo} <button onClick={handleRemove}> x</button></li>)}

            </div>
        </ul>
        </div>
    )
}

export default TodoList;
